import React from "react";
import { Platform ,Text} from "react-native";
import { createAppContainer } from "react-navigation";
import { createStackNavigator } from "react-navigation-stack";
import { createBottomTabNavigator } from "react-navigation-tabs";
import { createMaterialBottomTabNavigator } from "react-navigation-material-bottom-tabs";
import { createDrawerNavigator } from "react-navigation-drawer";
import { Ionicons } from "@expo/vector-icons";

import CategoriesScreen from "../screens/CategoriesScreen";
import CategoryMealsScreen from "../screens/CategoryMealsScreen";
import MealDetailScreen from "../screens/MealDetailScreen";
import FavouritesScreen from "../screens/FavouritesScreen";
import FiltersScreen from "../screens/FiltersScreen";
import Colors from "../constants/Colors";

const defaultStackNavOptions = {
  headerStyle: {
    backgroundColor: Platform.OS === "android" ? Colors.primaryColor : "",
  },
  headerTitleStyle:{
    fontFamily:'open-sans-bold'
  },
  headerBackTitleStyle:{
    fontFamily:'open-sans'
  },
  headerTintColor: Platform.OS === "android" ? "white" : Colors.primaryColor,
};

//categories,categorymeals,detail gibi porperty isimleri istedigimiz gibi olabilir.2cesit yapabiliriz ekranları:
//categories:ekran diyerek kısa yoldan verebiliriz yada c.meals:süslü parantezler ile uzun yoldan verebiliriz.
//uzun yoldakinde screen parametresi gibi birkac tane daha parametre var gerekirse verilebilir.
//navigation mantık olarak;stacknavigator diyip ekranları belirliyoruz.sonra createappcontainer metodu ile bu stacki alıyoruz
const MealsNavigator = createStackNavigator(
  {
    Categories: {
      screen: CategoriesScreen,
    },
    CategoryMeals: {
      screen: CategoryMealsScreen,
    },
    MealDetail: MealDetailScreen,
  },
  {
    //createstacknavigatorun 2.parametresi bu
    // initialRouteName:'Categories',baslangıc ekranı ayarlar.ama zaten categories baslangıc
    defaultNavigationOptions: {
      //üstteki 3 ekrandada default olarak asagıdaki stiller olacak.
      defaultStackNavOptions,
    },
  }
);

const FavNavigator = createStackNavigator(
  {
    Favourites: FavouritesScreen,
    MealDetail: MealDetailScreen,
  },
  {
    //createstacknavigatorun 2.parametresi bu
    // initialRouteName:'Categories',baslangıc ekranı ayarlar.ama zaten categories baslangıc
    defaultNavigationOptions: {
      //üstteki 3 ekrandada default olarak asagıdaki stiller olacak.
      defaultStackNavOptions,
    },
  }
);

const tabScreenConfig = {
  Meals: {
    screen: MealsNavigator,
    navigationOptions: {
      //tab navigation icinde stack navigation kullandıgımız icin navigationoptions kullandık!!!!
      //meals tabı için icon yaptık.tintcoloru alttaki activetintten alıyor!
      tabBarIcon: (tabInfo) => {
        return (
          <Ionicons name="ios-restaurant" size={25} color={tabInfo.tintColor} />
        );
      },
      tabBarColor: Colors.primaryColor,
      tabBarLabel: Platform.OS==='android'?<Text style={{fontFamily:'open-sans-bold'}}>Meals</Text>:'Meals'
    },
  },
  Favourites: {
    screen: FavNavigator,
    navigationOptions: {
      tabBarIcon: (tabInfo) => {
        return <Ionicons name="ios-star" size={25} color={tabInfo.tintColor} />;
      },
      tabBarColor: Colors.accentColor,
      tabBarLabel: Platform.OS==='android'?<Text style={{fontFamily:'open-sans-bold'}}>Favourites</Text>:'Favourites'
    },
  },
};

const MealsFavTabNavigator =
  Platform.OS === "android"
    ? createMaterialBottomTabNavigator(tabScreenConfig, {
        activeColor: "white",
        shifting: true,
      })
    : createBottomTabNavigator(tabScreenConfig, {
        tabBarOptions: {
          labelStyle:{
            fontFamily:'open-sans'
          },
          activeTintColor: Colors.accentColor,
        },
      });

const FilterNavigator = createStackNavigator(
  {
    Filters: FiltersScreen,
  },
  {
    /*
    navigationOptions:{//drawerdaki filterın ismini boyle değiştirebiliriz.ilk yontem bu.2.yol alttaki mealsfavstaki gibi!
      drawerLabel:'Filters!!!'
    },*/

    //createstacknavigatorun 2.parametresi bu
    // initialRouteName:'Categories',baslangıc ekranı ayarlar.ama zaten categories baslangıc
    defaultNavigationOptions: {
      //üstteki 3 ekrandada default olarak asagıdaki stiller olacak.
      defaultStackNavOptions,
    },
  }
);

const MainNavigator = createDrawerNavigator({
  //tabnavigatordede drawer navigatordede verecegimiz ekranları stacknav.yapıp verdik
  MealsFavs: {screen:MealsFavTabNavigator,navigationOptions:{
    drawerLabel:'Meals'
  }},
  Filters: FilterNavigator,
},{
  contentOptions:{
    activeTintColor:Colors.accentColor,
    labelStyle:{
      fontFamily:'open-sans-bold'
    }
  }
});

//export default createAppContainer(MealsNavigator);
export default createAppContainer(MainNavigator); //artık bunu verdik.çünkü diğer stack navigoturu meals tabı olarak
//verdik.

//navigation için npm install react-navigation yazıcaz terminale.
//npm install react-navigation-stack @react-native-community/masked-view react-native-safe-area-context
//ile react-navigation-stack kutuphanesini ekledik.
//tab navigation kullanmak için;
//npm install react-navigation-tabs paketini yukledik.
//materialbottomnavigator kullanmak için npm install react-navigation-material-bottom-tabs react-native-paper yukluyoruz.
//drawer navigation kullanmak icin;
//npm install react-navigation-drawer paketini yukledik.
