import React from "react";
import {
  View,
  Text,
  StyleSheet,
  Button,
  FlatList,
  TouchableOpacity,
} from "react-native";

import { HeaderButtons, Item } from "react-navigation-header-buttons";
import HeaderButton from "../components/HeaderButton";
import { CATEGORIES } from "../data/dummy-data";
import CategoryGridTile from "../components/CategoryGridTile";

const CategoriesScreen = (props) => {
  const renderGridItem = (itemData) => {
    //item dummydatadaki her bir item demek.item.title ise bu itemin titlesi demek.color gibi
    return (
      <CategoryGridTile
        title={itemData.item.title}
        color={itemData.item.color}
        onSelect={() => {
          props.navigation.navigate({
            routeName: "CategoryMeals",
            params: {
              categoryId: itemData.item.id,
            },
          });
          //props.navigation.navigate('CategoryMeals',{categoryId:datamız})//boylede yapabilirdik aynısı usttekinin.
        }}
      />
    );
  };

  return (
    <FlatList
      keyExtractor={(item, index) => item.id} //bu keyextractor eski versiyonlarda gerekli.ama yinede kullanıdık gostermek icin.
      data={CATEGORIES}
      renderItem={renderGridItem}
      numColumns={2}
    />

    /*
    <View style={StyleSheet.screen}>
      <Text>The Categories Screen!</Text>
      <Button title="Go to Meals!" onPress={()=>{
        props.navigation.navigate({routeName:'CategoryMeals'});
        //props.navigation.replace('CategoryMeals');bunun anlamı;sonraki sayfaya gidiyoruz ama bu sayfa artık yok oluyor.
        //yani üstte artık back butonu yok.category meal screenine back butonu koymustuk.artık o butonda çalısmıyor!!
      }}/>
    </View>*/
  );
};

CategoriesScreen.navigationOptions = (navData) => {
  return {
    headerTitle: "Meal Categories",
    headerLeft:()=> (
      <HeaderButtons HeaderButtonComponent={HeaderButton}>
        <Item
          title="Menu"
          iconName="ios-menu"
          onPress={() => {
            navData.navigation.toggleDrawer();
          }}
        />
      </HeaderButtons>
    ),
  };
};

const styles = StyleSheet.create({
  screen: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
});

export default CategoriesScreen;
